import React, { useContext } from 'react';
import logo from './logo.svg';
import './App.css';
import Login from './Login'
import Core from './Core';
import AuthContext from './AuthContext'
import ProtectedRoute from './ProtectedRoute'
import { BrowserRouter as Router, Route } from 'react-router-dom';
import PayrollPalClient from './payroll-pal-client';

class App extends React.Component {
  state = {
    isAuthenticated: window.localStorage.getItem('auth_client').isAuthenticated,
    authToken: window.localStorage.getItem('auth_client').authToken,
    login: (username, password) => {
        PayrollPalClient.login(username, password);
      }
    }
  render(){
    return(
      <AuthContext.Provider value={this.state}>
        <Router>
          <Route path="/login" component={Login} />
          <ProtectedRoute path="/core" component={Core} />
        </Router> 
      </AuthContext.Provider>
    )
  }
}

export default App
