import React, { useContext, useEffect, useState } from "react";
import { CoreContext } from './Core'
function EditEntry(props) {
    let coreContext = useContext(CoreContext)
    const [newPunchCounter, setNewPunchCounter] = useState(0);
    const [ punches, setPunches ] = useState(props.entry.punches)
    const parentProps = props;
    const Punch = (props) => {

        return (
            <div>
                <select onChange={updatePunch(props.index)} className="punchType" defaultValue={props.type} id={props.id}>
                    <option value="work" >Work</option>
                    <option value="meal" >Meal</option>
                    <option value="bereavment" >Bereavement</option>
                    <option value="holiday" >Holiday</option>
                    <option value="jury_duty" >Jury Duty</option>
                    <option value="personal_time_off" >Personal Time Off</option>
                    <option value="sick" >Sick</option>
                    <option value="unpaid_time" >Unpaid Time</option>
                    <option value="vacation" >Vacation</option>
                </select>
                <input defaultValue={props.start} type="text" placeholder="Start"name="start" />
                <input defaultValue={props.end} type="text" placeholder="End"name="end" />
             </div>
        )
    }
    const updatePunch = (index, event) => {
        switch(event.target.class){
            case 'updatePunch':

                break;
            case 'start':

                break;
            case 'end':

                break;

        }
    }
    const appendPunch = () => {
        setNewPunchCounter(newPunchCounter+1)
    }

    return(
        <div className="modal fade" id={`edit-entry-${props.index}`} tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                    <h5 className="pt-mono display-4 modal-title" id="exampleModalLabel">Edit Entry</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                    </div>
                    <div id={`edit-entry-body-${props.index}`}className="modal-body">

                        { punches ? punches.map((punch, i) => { return <Punch index={i} type={punch[0]} start={punch[1]} end={punch[2]} />}) : null }
                        { Array.from(Array(newPunchCounter), (e, i) => { return <Punch index={i+punches.length} key={i}  />}) }

                        <div onClick={appendPunch} className="btn pill btn-dark">Add punch</div>
                    </div>
                    <div className="modal-footer">
                    <button type="button" data-dismiss="modal" name="save" className="btn pill px-3 btn-success">Save</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EditEntry;
