import React, { useState, useEffect } from "react";
import Entry from "./Entry";
import EntryHeading from "./EntryHeading";
import PayrollPalClient from '../payroll-pal-client'

function EntriesList(props) {
    const [payPeriodStart, updatePayPeriodStart] = useState()
    const [payPeriodEnd, updatePayPeriodEnd] = useState()
    setCurrentPayPeriodInputs = (start, end) => {
        document.getElementById("start").value = start
        document.getElementById("end").value = end
        // this maybe should be in TimeCard.js but i'm 
        // torn between keeping the hooks together and keeping the
        // hook 'hierachy' simple
    }
    useEffect( () => {
        PayrollPalClient.getEntries(payPeriodStart, payPeriodEnd);
        setCurrentPayPeriodInputs(payPeriodStart, payPeriodEnd)
    });

    return(
        <div>
            <EntryHeading 
                    payPeriodStart={props.payPeriodStart} 
                    payPeriodEnd={props.payPeriodEnd} 
                    updatePayPeriodStart={props.updatePayPeriodStart} 
                    updatePayPeriodEnd={props.updatePayPeriodEnd}
            />
            <Entry />
        </div>
    )

}

export default EntriesList;
