import React, { useContext, useEffect, useState } from "react";
import { CoreContext } from './Core'
function EditEntry(props) {
    let coreContext = useContext(CoreContext)
    let [punchCounter, setPunchCounter] = useState(0);

    let Punch = (props) => {
        return (
            <div>
                <select id="type_0">
                    <option value="-1" >Work</option>
                    <option value="-2" >Meal</option>
                    <option value="3" >Bereavement</option>
                    <option value="1" >Holiday</option>
                    <option value="4" >Jury Duty</option>
                    <option value="5" >Personal Time Off</option>
                    <option value="7" >Sick</option>
                    <option value="2" >Unpaid Time</option>
                    <option value="8" >Vacation</option>
                </select>
                <input type="text" placeholder="Start"name="start" />
                <input type="text" placeholder="End"name="end" />
             </div>
        )
    }



    let appendPunch = () => {
        setPunchCounter(punchCounter+1)
    }
    return(
        <div className="modal fade" id={`edit-entry-${props.index}`} tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                    <h5 className="pt-mono display-4 modal-title" id="exampleModalLabel">Edit Entry</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                    </div>
                    <div id={`edit-entry-body-${props.index}`}className="modal-body">
                    {Array.from(Array(punchCounter), (e, i) => {
                        return <Punch id={`${props.index}-${e}`} />
                    })}
                        <div onClick={appendPunch} className="btn pill btn-dark">Add punch</div>
                    </div>
                    <div className="modal-footer">
                    <button type="button" data-dismiss="modal" name="save" className="btn pill px-3 btn-success">Save</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EditEntry;
