import React from "react";
import { payPeriodContext } from './EntryHeading'

class TimeCard extends React.Component {
    constructor(props){
        super(props)
        this.el = document.createElement("div");
        this.props.context = this.props.context.bind(this)
    }
    updateFields(e) {
        e.preventDefault();
        let start = document.getElementById("start").value
        let end = document.getElementById("end").value
        this.props.context.updatePayPeriodStart(start)
        this.props.context.updatePayPeriodEnd(end)
        document.getElementById('time-card').modal('hide')
    }
    render(){
        return(
            <div className="modal fade" id="time-card" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLabel">New Entry</h5>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                        </button>
                        </div>
                        <form onSubmit={this.updateFields}>
                            <div className="modal-body" id="new">
                                <input id="start" type="date" placeholder="start"/><br></br>
                                <input id="end" type="date" placeholder="end"/>
                            </div>
                            <div className="modal-footer">
                            <button type="submit"name="save_as_draft" className="btn btn-secondary">Save as Draft</button>
                            <button type="submit" name="save" className="btn btn-primary">Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default (props) => ( 
    <payPeriodContext.Consumer>
    {
        (context) => <TimeCard {...props} context={context} />
    }
    </payPeriodContext.Consumer>

);
