class PayrollPalClient  {
    constructor(props){
        this.authToken = null;
        window.localStorage.setItem('authToken', null)

    }
    static login(username, password){
        let ppc = new PayrollPalClient()
        ppc.isAuthenticated = true;
        ppc.authToken = "AUTH-TOKEN-XXXXXXX";
        window.localStorage.setItem('auth_client', ppc)
        return ppc
    }

    setAuthToken(token){
        this.authToken = token;
        window.localStorage.setItem('authToken', token)
    }
    setIsAuthenticated(val=false){
        this.isAuthenticated = val;
        window.localStorage.setItem('isAuthenticated', val)
    }

    static setLocalStorage(key, value){
        window.localStorage.setItem(key, value)
        return window.localStorage.getItem(key)
    }
    static getFromLocalStorage(key){
        return window.localStorage.getItem(key)
    }
    static deleteFromLocalStorage(key){
        window.localStorage.removeItem(key)
        return window.localStorage
    }

    logout(){
        this.token = null
        this.isAuthenticated = false
    }
}

export default PayrollPalClient;
