class PayrollPalClient  {
    static login(username, password){
        let ppc = new PayrollPalClient()
        ppc.setIsAuthenticated(true);
        ppc.setAuthToken("AUTH-TOKEN-XXXXXXX");
        return ppc
    }
    logout(){
        this.authToken = null;
        this.deleteAuthToken();
        this.isAuthenticated = false;
        this.deleteIsAuthenticated();
    }

    static getAuthToken(){
        return PayrollPalClient.getCookie('authToken')
    }
    static getIsAuthenticated(){
        return PayrollPalClient.getCookie('isAuthenticated')
    }

    setAuthToken(token){
        this.authToken = token;
        PayrollPalClient.setCookie({'authToken': token})
    }
    setIsAuthenticated(val){
        console.log(val)
        this.isAuthenticated = val;
        PayrollPalClient.setCookie({'isAuthenticated': val})
    }

    deleteAuthToken(){
        this.authToken = undefined;
        this.deleteCookie('authToken')
    }
    deleteIsAuthenticated(){
        this.isAuthenticated = undefined;
        this.deleteCookie('isAuthenticated')
    }

    static getCookie(){
        return document.cookie
    }

    static setCookie(obj){
        let cookieAsObj = PayrollPalClient.cookieAsObj()
        Object.keys(obj).map(key => {
            cookieAsObj[key] = obj[key]
        })
        return this.cookieObjToCookieString(cookieAsObj)
    }

    static getCookieValue(key){
        let cookieAsObj = PayrollPalClient.cookieAsObj()
        return cookieAsObj[key]
    }

    static deleteCookie(key){
        let cookieAsObj = PayrollPalClient.cookieAsObj()
        delete cookieAsObj[key]
        return this.cookieObjToCookieString(cookieAsObj)
    }

    static cookieAsObj(){
        let cookie = document.cookie;
        cookie = cookie.split(";");
        cookie = [cookie.map(el => el.split("="))]
        let cookieAsObj = {}
        for (let c in cookie){
            cookieAsObj[c[0]] = c[1]
        }

        return cookieAsObj
    }

    static cookieObjToCookieString(cookieObj){
        let cookie = '';
        return Object.keys(cookieObj).map(key => {
            cookie += `${key}=${cookieObj[key]}; `
        })
        return cookie
    }

}

export default PayrollPalClient;
