import React, { useEffect, useState } from "react";
function EditEntry(props) {
    const [punchCounter, setPunchCounter] = useState(props.entry.punches ? props.entry.punches.length : 0);
    const [newPunchCounter, setNewPunchCounter] = useState(0)
    const Punch = (props) => {
        return (
            <div>
                <select name="punch-type" className="punchType" onChange={updatePunch} defaultValue={props.type} id={props.punchIndex}>
                    <option value="work" >Work</option>
                    <option value="meal" >Meal</option>
                    <option value="bereavment" >Bereavement</option>
                    <option value="holiday" >Holiday</option>
                    <option value="jury_duty" >Jury Duty</option>
                    <option value="personal_time_off" >Personal Time Off</option>
                    <option value="sick" >Sick</option>
                    <option value="unpaid_time" >Unpaid Time</option>
                    <option value="vacation" >Vacation</option>
                </select>
                <input defaultValue={props.start} onChange={updatePunch} className="start" type="text" placeholder="Start"name="start" />
                <input defaultValue={props.end} onChange={updatePunch} className="end" type="text" placeholder="End"name="end" />
             </div>
        )
    }

    const appendPunch = () => {
        setPunchCounter(punchCounter+1)
        setNewPunchCounter(newPunchCounter+1)
    }
    const updatePunch = (e) => {
        let punchIndex = e.target.id
        switch(e.target.name) {
            case 'punch-type':
                console.log(props)
                props.entry.punches[punchIndex][0] = [e.target.value]
                break;
            case 'start':
                props.entry.punches[punchIndex][1] = [e.target.value]
                break;
            case 'end':
                props.entry.punches[punchIndex][2] = [e.target.value]
                break;
        }
    }
    const updateEntry = (e) => {

    }
    return(
        <div className="modal fade" id={`edit-entry-${props.entryIndex}`} tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                    <h5 className="pt-mono display-4 modal-title" id="exampleModalLabel">Edit Entry</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                    </div>
                    <div id={`edit-entry-body-${props.index}`}className="modal-body">

                        { props.entry.punches ? props.entry.punches.map((punch, i) => { return <Punch entryIndex={props.entryIndex} type={punch[0]} start={punch[1]} end={punch[2]} punchIndex={i} /> }) : null }
                        { Array.from(Array(newPunchCounter), (e, i) => { return <Punch entryIndex={props.entryIndex}  key={i} punchIndex={i+punchCounter}  />}) }

                        <div onClick={appendPunch} className="btn pill btn-dark">Add punch</div>
                    </div>
                    <div className="modal-footer">
                    <button onClick={updateEntry} type="button" data-dismiss="modal" name="save" className="btn pill px-3 btn-success">Save</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EditEntry;
